mapper.select:
SELECT eh.equipment_history_no equipmentHistoryNo, eh.equipment_no equipmentNo, e.equipment_name equipmentName, eh.member_id memberId, eh.equipment_begindate equipmentBegindate, eh.equipment_enddate equipmentEnddate FROM equipment_history eh INNER JOIN equipment e ON eh.equipment_no = e.equipment_no
eh.equipment_no = #{equipmentNo}
AND member_id LIKE '%' #{memberId} '%'
ORDER BY eh.equipment_history_no DESC LIMIT #{beginRow}, #{rowPerPage}

SELECT COUNT(*) FROM equipment_history eh INNER JOIN equipment e ON eh.equipment_no = e.equipment_no
eh.equipment_no = #{equipmentNo}
AND member_id LIKE '%'#{memberId}'%'

SELECT eh.equipment_history_no equipmentHistoryNo, eh.equipment_no equipmentNo, e.equipment_name equipmentName, e.equipment_status equipmentStatus, eh.member_id memberId, eh.equipment_begindate equipmentBegindate, eh.equipment_enddate equipmentEnddate FROM equipment_history eh INNER JOIN equipment e ON eh.equipment_no = e.equipment_no
eh.member_id = #{memberId}
AND e.equipment_name LIKE '%' #{equipmentName} '%'
ORDER BY eh.equipment_history_no DESC LIMIT #{beginRow}, #{rowPerPage}

SELECT COUNT(*) FROM equipment_history eh INNER JOIN equipment e ON eh.equipment_no = e.equipment_no
eh.member_id = #{memberId}
AND e.equipment_name LIKE '%' #{equipmentName} '%'
